import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import questions from "../data/questions";
import { runAssessment } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import { buildPayload } from "../utils/payloadBuilder";

/* ------------------ ANIMATIONS ------------------ */
const pageFade = {
  initial: { opacity: 0, y: 40 },
  animate: { opacity: 1, y: 0 },
  exit: { opacity: 0, y: -40 },
};

/* ------------------ QUESTION SECTIONS ------------------ */
const QUESTION_SECTIONS = [
  { name: "COGNITIVE", start: 0, end: 11 },
  { name: "NUMERACY", start: 12, end: 19 },
  { name: "ACADEMIC", start: 20, end: 29 },
  { name: "VERBAL", start: 30, end: 35 },
  { name: "INTEREST", start: 36, end: 43 },
  { name: "DISCIPLINE", start: 44, end: 51 },
  { name: "RISK", start: 52, end: 59 },
];

const AssessmentPage = () => {
  const navigate = useNavigate();
  const [darkMode, setDarkMode] = useState(false);
  const [step, setStep] = useState("profile");

  /* ------------------ PROFILE DATA ------------------ */
  const [profileData, setProfileData] = useState({
    name: "",
    phone: "",
    educationLevel: "",
    educationStatus: "",
    field: "",
    marks: {},
    stream: "",
    board: "",
    language: "",
    budget: "",
    abroad: "",
  });

  /* ------------------ QUESTIONS ------------------ */
  const [answers, setAnswers] = useState({});
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  /* ------------------ PROFILE HANDLER ------------------ */
  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfileData((p) => ({ ...p, [name]: value }));
  };

  const handleMarksChange = (key, value) => {
    setProfileData((p) => ({
      ...p,
      marks: { ...p.marks, [key]: value },
    }));
  };

  const isProfileComplete = () => {
    const { name, phone, educationLevel, educationStatus, stream, board, language, budget, abroad } = profileData;
    return (
      name &&
      phone &&
      educationLevel &&
      educationStatus &&
      stream &&
      board &&
      language &&
      budget &&
      abroad
    );
  };

  /* ------------------ ANSWER HANDLER (AUTO SAVE) ------------------ */
  const handleSelect = (qid, optionIndex) => {
    setAnswers((prev) => ({ ...prev, [qid]: optionIndex }));
  };

  const nextQuestion = () => {
    if (answers[questions[currentQuestionIdx].id] === undefined) {
      alert("Please answer this question");
      return;
    }
    setCurrentQuestionIdx((p) => p + 1);
  };

  const prevQuestion = () => {
    if (currentQuestionIdx > 0) setCurrentQuestionIdx((p) => p - 1);
  };

  /* ------------------ FINAL SUBMIT ------------------ */
  const handleSubmit = async () => {
    try {
      const payload = buildPayload({ profileData, answers });
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch {
      alert("Assessment failed");
    }
  };

  const currentQuestion = questions[currentQuestionIdx];
  const progress = ((currentQuestionIdx + 1) / questions.length) * 100;

  const activeSection = QUESTION_SECTIONS.find(
    (s) => currentQuestionIdx >= s.start && currentQuestionIdx <= s.end
  );

  /* ------------------ UI ------------------ */
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 to-purple-100 dark:from-slate-900 dark:to-black">

      {/* HEADER */}
      <header className="fixed top-0 w-full z-50 backdrop-blur bg-white/70 dark:bg-black/60">
        <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between">
          <h1 className="font-bold text-xl">CollegeReview<span className="text-indigo-500">Z</span></h1>
          <button onClick={() => setDarkMode(!darkMode)}>{darkMode ? "‚òÄÔ∏è" : "üåô"}</button>
        </div>
      </header>

      <div className="h-28" />

      {/* ================= PROFILE ================= */}
      {step === "profile" && (
        <motion.section {...pageFade} className="max-w-4xl mx-auto bg-white/80 dark:bg-slate-900 p-8 rounded-3xl">
          <h2 className="text-xl font-semibold mb-6">üéì Student Details</h2>

          <div className="grid md:grid-cols-2 gap-4">
            <input name="name" placeholder="Full Name *" onChange={handleProfileChange} className="input" />
            <input name="phone" placeholder="Phone Number *" onChange={handleProfileChange} className="input" />

            <select name="educationLevel" onChange={handleProfileChange} className="input">
              <option value="">Education Level *</option>
              <option>9th</option><option>10th</option><option>11th</option>
              <option>12th</option><option>Graduation</option>
            </select>

            <select name="educationStatus" onChange={handleProfileChange} className="input">
              <option value="">Status *</option>
              <option>Pursuing</option>
              <option>Completed</option>
            </select>

            {(profileData.educationLevel === "11th" ||
              profileData.educationLevel === "12th" ||
              profileData.educationLevel === "Graduation") && (
              <select name="field" onChange={handleProfileChange} className="input">
                <option value="">Field *</option>
                {profileData.educationLevel === "Graduation"
                  ? ["BA","BSC","BCOM","BTECH","BCA","LAW","MBBS"].map(f => <option key={f}>{f}</option>)
                  : ["Science","Arts","Commerce"].map(f => <option key={f}>{f}</option>)
                }
              </select>
            )}
          </div>

          <h3 className="mt-6 font-semibold">Marks (%)</h3>
          <div className="grid md:grid-cols-3 gap-4 mt-2">
            {["9th","10th","11th","12th","Graduation"].map(y => (
              <input
                key={y}
                placeholder={`${y} %`}
                onChange={(e) => handleMarksChange(y, e.target.value)}
                className="input"
              />
            ))}
          </div>

          <div className="grid md:grid-cols-2 gap-4 mt-6">
            <input name="stream" placeholder="Stream *" onChange={handleProfileChange} className="input" />
            <input name="board" placeholder="Board *" onChange={handleProfileChange} className="input" />
            <select name="language" onChange={handleProfileChange} className="input">
              <option value="">Language *</option>
              {["Hindi","English","Bengali","Tamil","Telugu","Marathi","Gujarati","Urdu"].map(l => <option key={l}>{l}</option>)}
            </select>
            <select name="budget" onChange={handleProfileChange} className="input">
              <option value="">Budget *</option>
              {["5-6","6-7","7-10","10-15","15-20","20-30"].map(b => <option key={b}>{b} Lakhs</option>)}
            </select>
            <select name="abroad" onChange={handleProfileChange} className="input">
              <option value="">Open To Abroad *</option>
              <option>Yes</option>
              <option>No</option>
            </select>
          </div>

          <div className="text-right mt-8">
            <button
              disabled={!isProfileComplete()}
              onClick={() => setStep("questions")}
              className="px-8 py-3 bg-indigo-500 text-white rounded-xl disabled:opacity-40"
            >
              Save & Continue ‚Üí
            </button>
          </div>
        </motion.section>
      )}

      {/* ================= QUESTIONS ================= */}
      {step === "questions" && (
        <div className="max-w-4xl mx-auto pb-24 px-4">

          <div className="text-center mb-4 font-semibold text-indigo-600">
            {activeSection?.name}
          </div>

          <div className="h-2 bg-white/40 rounded-full mb-6">
            <motion.div className="h-full bg-indigo-500" animate={{ width: `${progress}%` }} />
          </div>

          <AnimatePresence mode="wait">
            <motion.div key={currentQuestion.id} {...pageFade} className="bg-white dark:bg-slate-900 p-8 rounded-3xl">
              <h3 className="font-semibold mb-4">{currentQuestion.text}</h3>

              {currentQuestion.options.map((opt, idx) => (
                <label key={idx} className="block mb-3 cursor-pointer">
                  <input
                    type="radio"
                    className="hidden"
                    checked={answers[currentQuestion.id] === idx}
                    onChange={() => handleSelect(currentQuestion.id, idx)}
                  />
                  <div className={`p-4 rounded-xl border ${answers[currentQuestion.id] === idx ? "bg-indigo-500 text-white" : ""}`}>
                    {typeof opt === "string" ? opt : opt.label}
                  </div>
                </label>
              ))}

              <div className="flex justify-between mt-6">
                <button onClick={prevQuestion} disabled={currentQuestionIdx === 0} className="btn">
                  Previous
                </button>

                {currentQuestionIdx < questions.length - 1 ? (
                  <button onClick={nextQuestion} className="btn-primary">
                    Save & Continue ‚Üí
                  </button>
                ) : (
                  <button onClick={handleSubmit} className="btn-primary bg-green-500">
                    Submit Assessment
                  </button>
                )}
              </div>
            </motion.div>
          </AnimatePresence>
        </div>
      )}
    </div>
  );
};

export default AssessmentPage;
