import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import questions from "../data/questions";
import { runAssessment } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import { buildPayload } from "../utils/payloadBuilder";

const fade = {
  initial: { opacity: 0, y: 30 },
  animate: { opacity: 1, y: 0 },
  exit: { opacity: 0, y: -30 },
};

const AssessmentPage = () => {
  const navigate = useNavigate();
  const [darkMode, setDarkMode] = useState(false);

  /* üîπ UPDATED PROFILE STATE */
  const [profileData, setProfileData] = useState({
    name: "",
    phone: "",
    educationLevel: "",
    studyField: "",
    marks: {},
    stream: "",
    board: "",
    language: "",
    budget: "",
    abroad: "No",
  });

  const [answers, setAnswers] = useState({});
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfileData((p) => ({ ...p, [name]: value }));
  };

  const handleMarksChange = (key, value) => {
    setProfileData((p) => ({
      ...p,
      marks: { ...p.marks, [key]: value },
    }));
  };

  /* üîπ EDUCATION RULE ENGINE */
  const getMarksFields = () => {
    const level = profileData.educationLevel;
    if (!level) return [];

    switch (level) {
      case "9th completed":
        return ["9th"];
      case "10th pursuing":
        return ["9th"];
      case "10th completed":
        return ["9th", "10th"];
      case "11th pursuing":
        return ["9th", "10th"];
      case "11th completed":
        return ["9th", "10th", "11th"];
      case "12th pursuing":
        return ["10th", "11th"];
      case "12th completed":
        return ["10th", "11th", "12th"];
      case "Graduation pursuing":
        return ["10th", "12th"];
      case "Graduation completed":
        return ["10th", "12th", "Graduation"];
      default:
        return [];
    }
  };

  const needsField =
    profileData.educationLevel?.includes("11th") ||
    profileData.educationLevel?.includes("12th") ||
    profileData.educationLevel?.includes("Graduation");

  const handleSubmit = async () => {
    if (!profileData.name || !profileData.phone) {
      alert("Name and Phone are required");
      return;
    }

    try {
      const payload = buildPayload({ profileData, answers });
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch {
      alert("Assessment failed");
    }
  };

  const currentQuestion = questions[currentQuestionIdx];
  const progress = ((currentQuestionIdx + 1) / questions.length) * 100;

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 to-purple-100 dark:from-slate-900 dark:to-black transition-all">

      {/* HEADER */}
      <header className="fixed top-0 w-full z-50 backdrop-blur bg-white/70 dark:bg-black/60">
        <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between">
          <h1 className="font-bold text-xl">CollegeReviewZ</h1>
          <button onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? "‚òÄÔ∏è" : "üåô"}
          </button>
        </div>
      </header>

      <div className="h-28" />

      {/* PROFILE */}
      <motion.div {...fade} className="max-w-4xl mx-auto bg-white/80 dark:bg-slate-900 p-8 rounded-3xl shadow mb-10">

        <h2 className="text-xl font-semibold mb-6">üë§ Student Profile</h2>

        {/* Name & Phone */}
        <div className="grid md:grid-cols-2 gap-5 mb-6">
          <input name="name" placeholder="Full Name *" onChange={handleProfileChange} className="p-4 rounded-xl border" />
          <input name="phone" placeholder="Phone No *" onChange={handleProfileChange} className="p-4 rounded-xl border" />
        </div>

        {/* Education Level */}
        <select name="educationLevel" onChange={handleProfileChange} className="w-full p-4 rounded-xl border mb-6">
          <option value="">Educational Details *</option>
          {[
            "9th pursuing","9th completed","10th pursuing","10th completed",
            "11th pursuing","11th completed","12th pursuing","12th completed",
            "Graduation pursuing","Graduation completed"
          ].map(l => <option key={l}>{l}</option>)}
        </select>

        {/* Study Field */}
        {needsField && (
          <select name="studyField" onChange={handleProfileChange} className="w-full p-4 rounded-xl border mb-6">
            <option value="">Field of Study *</option>
            {["SCIENCE","ARTS","COMMERCE","BA","BSC","BCOM","BTECH","BCA","LAW","MBBS"].map(f => (
              <option key={f}>{f}</option>
            ))}
          </select>
        )}

        {/* MARKS */}
        {getMarksFields().length > 0 && (
          <div className="mb-6">
            <h3 className="font-medium mb-3">Marks (%)</h3>
            <div className="grid md:grid-cols-3 gap-4">
              {getMarksFields().map(m => (
                <input
                  key={m}
                  placeholder={`${m} %`}
                  onChange={(e) => handleMarksChange(m, e.target.value)}
                  className="p-3 rounded-xl border"
                />
              ))}
            </div>
          </div>
        )}

        {/* Other Required Fields */}
        <div className="grid md:grid-cols-2 gap-5">
          <input name="stream" placeholder="Stream *" onChange={handleProfileChange} className="p-4 rounded-xl border" />
          <input name="board" placeholder="Board *" onChange={handleProfileChange} className="p-4 rounded-xl border" />
        </div>

        <select name="language" onChange={handleProfileChange} className="w-full p-4 rounded-xl border mt-5">
          <option value="">Language *</option>
          {[
            "Assamese","Bengali","Bodo","Dogri","Gujarati","Hindi","Kannada",
            "Kashmiri","Konkani","Maithili","Malayalam","Manipuri","Marathi",
            "Nepali","Odia","Punjabi","Sanskrit","Santali","Sindhi","Tamil","Telugu","Urdu"
          ].map(l => <option key={l}>{l}</option>)}
        </select>

        <select name="budget" onChange={handleProfileChange} className="w-full p-4 rounded-xl border mt-5">
          <option value="">Budget</option>
          {["5-6","6-7","7-10","10-15","15-20","20-30"].map(b => (
            <option key={b}>{b} Lakhs</option>
          ))}
        </select>

        <select name="abroad" onChange={handleProfileChange} className="w-full p-4 rounded-xl border mt-5">
          <option>No</option>
          <option>Yes</option>
        </select>

      </motion.div>

      {/* QUESTIONS & SUBMIT ‚Üí UNCHANGED */}
      {/* YOUR EXISTING QUESTION UI CONTINUES BELOW */}
    </div>
  );
};

export default AssessmentPage;
