import { BrowserRouter, Routes, Route, useNavigate, useLocation } from "react-router-dom";
import React, { useState } from "react";
import questions from "/server/";
import logo from "./basiclogo1.ico";

/* ================= GLOBAL STYLES ================= */
const styles = {
  app: (dark) => ({
    minHeight: "100vh",
    background: dark ? "#0f172a" : "#f8fafc",
    color: dark ? "#e5e7eb" : "#111827",
    fontFamily: "Arial, sans-serif",
    paddingBottom: "40px",
  }),

  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "15px 25px",
    background: "#2563eb",
    color: "#fff",
  },

  logoBox: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
  },

  logo: {
    width: "40px",
    height: "40px",
  },

  card: {
    maxWidth: "520px",
    margin: "30px auto",
    background: "#fff",
    padding: "25px",
    borderRadius: "10px",
    boxShadow: "0 10px 25px rgba(0,0,0,0.1)",
  },

  input: {
    width: "100%",
    padding: "10px",
    margin: "8px 0",
    borderRadius: "6px",
    border: "1px solid #cbd5e1",
    fontSize: "14px",
  },

  button: {
    width: "100%",
    padding: "12px",
    marginTop: "12px",
    background: "#2563eb",
    color: "#fff",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontSize: "15px",
  },

  marksBox: {
    marginTop: "15px",
    padding: "10px",
    border: "1px dashed #94a3b8",
    borderRadius: "6px",
  },

  footer: {
    textAlign: "center",
    marginTop: "40px",
    color: "#64748b",
    fontSize: "13px",
  },
};

/* ================= ASSESSMENT PAGE ================= */
const AssessmentPage = () => {
  const navigate = useNavigate();
  const [dark, setDark] = useState(false);
  const [currentQ, setCurrentQ] = useState(0);
  const [answers, setAnswers] = useState({});
  const [marks, setMarks] = useState({});

  const [profile, setProfile] = useState({
    name: "",
    phone: "",
    class: "9th",
    status: "Pursuing",
    stream: "",
    board: "",
    language: "",
    budget: "",
  });

  const handleProfile = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  /* -------- MARKS LOGIC -------- */
  const getMarksFields = () => {
    const { class: c, status: s } = profile;

    if (c === "9th" && s === "Pursuing") return [];
    if (c === "9th" && s === "Completed") return ["9th"];

    if (c === "10th" && s === "Pursuing") return ["9th"];
    if (c === "10th" && s === "Completed") return ["9th", "10th"];

    if (c === "11th" && s === "Pursuing") return ["9th", "10th"];
    if (c === "11th" && s === "Completed") return ["9th", "10th", "11th"];

    if (c === "12th" && s === "Pursuing") return ["10th", "11th"];
    if (c === "12th" && s === "Completed") return ["10th", "11th", "12th"];

    if (c === "Graduation" && s === "Pursuing") return ["10th", "12th"];
    if (c === "Graduation" && s === "Completed")
      return ["10th", "12th", "Graduation"];

    return [];
  };

  const handleMarks = (key, value) => {
    setMarks({ ...marks, [key]: value });
  };

  /* -------- VALIDATION FIXED -------- */
  const validateProfile = () => {
    if (
      !profile.name ||
      !profile.phone ||
      !profile.board ||
      !profile.language ||
      !profile.budget
    ) {
      alert("Please fill all required fields");
      return;
    }
    setCurrentQ(1);
  };

  const handleAnswer = (option) => {
    setAnswers({ ...answers, [currentQ]: option });
    if (currentQ < questions.length) {
      setCurrentQ(currentQ + 1);
    } else {
      navigate("/report", { state: { profile, marks, answers } });
    }
  };

  return (
    <div style={styles.app(dark)}>
      <header style={styles.header}>
        <div style={styles.logoBox}>
          <img src={logo} style={styles.logo} />
          <h3>CollegeReviewZ</h3>
        </div>
        <button onClick={() => setDark(!dark)}>ðŸŒ™</button>
      </header>

      <div style={styles.card}>
        {currentQ === 0 ? (
          <>
            <h3>Student Details</h3>

            <input style={styles.input} name="name" placeholder="Name" onChange={handleProfile} />
            <input style={styles.input} name="phone" placeholder="Phone" onChange={handleProfile} />

            <select style={styles.input} name="class" onChange={handleProfile}>
              <option>9th</option>
              <option>10th</option>
              <option>11th</option>
              <option>12th</option>
              <option>Graduation</option>
            </select>

            <select style={styles.input} name="status" onChange={handleProfile}>
              <option>Pursuing</option>
              <option>Completed</option>
            </select>

            {(profile.class === "11th" ||
              profile.class === "12th" ||
              profile.class === "Graduation") && (
              <select style={styles.input} name="stream" onChange={handleProfile}>
                <option value="">Select Stream</option>
                <option>Science</option>
                <option>Arts</option>
                <option>Commerce</option>
                <option>BA</option>
                <option>BSc</option>
                <option>BCom</option>
                <option>BTech</option>
                <option>BCA</option>
                <option>LAW</option>
                <option>MBBS</option>
              </select>
            )}

            {getMarksFields().length > 0 && (
              <div style={styles.marksBox}>
                <strong>Marks (%)</strong>
                {getMarksFields().map((m) => (
                  <input
                    key={m}
                    style={styles.input}
                    placeholder={`${m} Percentage`}
                    onChange={(e) => handleMarks(m, e.target.value)}
                  />
                ))}
              </div>
            )}

            <button style={styles.button} onClick={validateProfile}>
              Start Assessment
            </button>
          </>
        ) : (
          <>
            <h4>
              Question {currentQ} / {questions.length}
            </h4>
            <p>{questions[currentQ - 1].text}</p>
            {questions[currentQ - 1].options.map((o, i) => (
              <button key={i} style={styles.button} onClick={() => handleAnswer(o)}>
                {o}
              </button>
            ))}
          </>
        )}
      </div>

      <footer style={styles.footer}>Â© 2026 CollegeReviewZ</footer>
    </div>
  );
};

/* ================= REPORT PAGE ================= */
const ReportPage = () => {
  const { state } = useLocation();
  if (!state) return <h3>No Data Found</h3>;

  return (
    <div style={{ padding: "30px" }}>
      <h2>Assessment Completed âœ…</h2>
      <pre>{JSON.stringify(state, null, 2)}</pre>
    </div>
  );
};

/* ================= APP ROUTER ================= */
export default function App() {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<AssessmentPage />} />
        <Route path="/report" element={<ReportPage />} />
      </Routes>
    </BrowserRouter>
  );
}
