// src/pages/AssessmentPage.jsx

import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import questions from "../data/questions";
import { runAssessment } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import { buildPayload } from "../utils/payloadBuilder";

const AssessmentPage = () => {
  const navigate = useNavigate();

  const [darkMode, setDarkMode] = useState(false);

  // ‚úÖ UPDATED PROFILE STATE
  const [profileData, setProfileData] = useState({
    stream: "",
    board: "",
    state: "",
    language: "",
    budget: "",
    abroad: "",
  });

  const [answers, setAnswers] = useState({});
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfileData((p) => ({ ...p, [name]: value }));
  };

  const handleSelect = (qid, optionIndex) => {
    setAnswers((prev) => ({ ...prev, [qid]: optionIndex }));
  };

  const handleNext = () => {
    if (currentQuestionIdx < questions.length - 1) {
      setCurrentQuestionIdx((p) => p + 1);
    }
  };

  const handlePrev = () => {
    if (currentQuestionIdx > 0) {
      setCurrentQuestionIdx((p) => p - 1);
    }
  };

  const handleSubmit = async () => {
    const isProfileComplete = Object.values(profileData).every(Boolean);

    if (!isProfileComplete) {
      alert("Please fill all required profile fields");
      return;
    }

    const payload = buildPayload({ profileData, answers });

    try {
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch (err) {
      alert("Assessment failed");
      console.error(err);
    }
  };

  const currentQuestion = questions[currentQuestionIdx];
  const progress =
    ((currentQuestionIdx + 1) / questions.length) * 100;

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-100 to-slate-200 dark:from-slate-900 dark:to-black text-slate-900 dark:text-slate-100 transition-colors duration-500">
      {/* HEADER */}
      <header className="fixed top-0 w-full z-50 bg-white/80 dark:bg-slate-900/80 backdrop-blur border-b">
        <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="font-bold text-xl">CollegeReviewZ</h1>
          <button onClick={() => setDarkMode(!darkMode)} className="text-xl">
            {darkMode ? "‚òÄÔ∏è" : "üåô"}
          </button>
        </div>
      </header>

      <div className="h-24" />

      {/* ================= PROFILE SECTION ================= */}
      <motion.div
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        className="max-w-3xl mx-auto bg-white dark:bg-slate-800 rounded-2xl shadow-xl p-6 mb-8"
      >
        <h2 className="text-lg font-semibold mb-4">Student Details</h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <select name="stream" onChange={handleProfileChange} className="p-3 rounded-lg">
            <option value="">Stream *</option>
            <option>Science</option>
            <option>Arts</option>
            <option>Commerce</option>
          </select>

          <input
            name="board"
            placeholder="Board *"
            onChange={handleProfileChange}
            className="p-3 rounded-lg"
          />

          <input
            name="state"
            placeholder="State *"
            onChange={handleProfileChange}
            className="p-3 rounded-lg"
          />

          <select name="language" onChange={handleProfileChange} className="p-3 rounded-lg">
            <option value="">Language *</option>
            {[
              "Assamese","Bengali","Bodo","Dogri","Gujarati","Hindi","Kannada",
              "Kashmiri","Konkani","Maithili","Malayalam","Manipuri","Marathi",
              "Nepali","Odia","Punjabi","Sanskrit","Santali","Sindhi","Tamil",
              "Telugu","Urdu"
            ].map((l) => (
              <option key={l}>{l}</option>
            ))}
          </select>

          <select name="budget" onChange={handleProfileChange} className="p-3 rounded-lg">
            <option value="">Annual Budget *</option>
            <option>4‚Äì5 Lakh</option>
            <option>5‚Äì6 Lakh</option>
            <option>6‚Äì7 Lakh</option>
            <option>7‚Äì10 Lakh</option>
            <option>10‚Äì15 Lakh</option>
            <option>15‚Äì20 Lakh</option>
            <option>20‚Äì30 Lakh</option>
          </select>

          <select name="abroad" onChange={handleProfileChange} className="p-3 rounded-lg">
            <option value="">Open To Abroad *</option>
            <option>Yes</option>
            <option>No</option>
          </select>
        </div>
      </motion.div>

      {/* ================= QUESTIONS ================= */}
      <div className="max-w-3xl mx-auto px-4 pb-16">
        <div className="h-2 bg-slate-300 dark:bg-slate-700 rounded-full mb-6">
          <motion.div className="h-full bg-blue-500" animate={{ width: `${progress}%` }} />
        </div>

        <AnimatePresence mode="wait">
          <motion.div
            key={currentQuestion.id}
            initial={{ opacity: 0, x: 80 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -80 }}
            className="bg-white dark:bg-slate-800 rounded-2xl shadow-lg p-6"
          >
            <h2 className="font-semibold mb-4">
              Question {currentQuestionIdx + 1} of {questions.length}
            </h2>

            <p className="mb-6">{currentQuestion.text}</p>

            <div className="space-y-3">
              {currentQuestion.options.map((opt, idx) => (
                <label key={idx} className="flex gap-3 p-3 border rounded-xl cursor-pointer">
                  <input
                    type="radio"
                    checked={answers[currentQuestion.id] === idx}
                    onChange={() => handleSelect(currentQuestion.id, idx)}
                  />
                  <span>{typeof opt === "string" ? opt : opt.label}</span>
                </label>
              ))}
            </div>

            <div className="flex justify-between mt-8">
              <button onClick={handlePrev} disabled={currentQuestionIdx === 0}>
                Previous
              </button>

              {currentQuestionIdx < questions.length - 1 ? (
                <button onClick={handleNext}>Save & Continue</button>
              ) : (
                <button onClick={handleSubmit}>Submit Assessment</button>
              )}
            </div>
          </motion.div>
        </AnimatePresence>
      </div>

      <footer className="text-center py-6 text-sm">
        ¬© 2026 CollegeReviewZ
      </footer>
    </div>
  );
};

export default AssessmentPage;
