import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import questions from "../data/questions";
import { runAssessment } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import { buildPayload } from "../utils/payloadBuilder";

const pageFade = {
  initial: { opacity: 0, y: 40 },
  animate: { opacity: 1, y: 0 },
  exit: { opacity: 0, y: -40 },
};

const AssessmentPage = () => {
  const navigate = useNavigate();
  const [darkMode, setDarkMode] = useState(false);
  const [step, setStep] = useState("profile");

  /** PROFILE DATA (EXTENDED, NOTHING REMOVED) */
  const [profileData, setProfileData] = useState({
    name: "",
    phone: "",
    educationLevel: "",
    educationStatus: "",
    field: "",
    marks: {
      class9: "",
      class10: "",
      class11: "",
      class12: "",
      graduation: "",
    },
    stream: "",
    board: "",
    state: "",
    language: "",
    budget: "",
    abroad: "",
  });

  /** QUESTIONS STATE */
  const [answers, setAnswers] = useState({});
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  /** HANDLERS */
  const handleProfileChange = (e) => {
    setProfileData((p) => ({ ...p, [e.target.name]: e.target.value }));
  };

  const handleMarksChange = (key, value) => {
    setProfileData((p) => ({
      ...p,
      marks: { ...p.marks, [key]: value },
    }));
  };

  const isProfileComplete =
    profileData.name &&
    profileData.phone &&
    profileData.educationLevel &&
    profileData.educationStatus &&
    profileData.stream &&
    profileData.board &&
    profileData.language &&
    profileData.budget &&
    profileData.abroad;

  /** MARK VISIBILITY LOGIC */
  const showMark = (cls) => {
    const l = profileData.educationLevel;
    const s = profileData.educationStatus;

    if (l === "9th") return s === "Completed" && cls === "class9";
    if (l === "10th")
      return cls === "class9" || (s === "Completed" && cls === "class10");
    if (l === "11th")
      return ["class9", "class10"].includes(cls) || (s === "Completed" && cls === "class11");
    if (l === "12th")
      return ["class10", "class11"].includes(cls) || (s === "Completed" && cls === "class12");
    if (l === "Graduation")
      return ["class10", "class12"].includes(cls) || (s === "Completed" && cls === "graduation");
    return false;
  };

  /** QUESTION LOGIC (UNCHANGED) */
  const handleSelect = (qid, optionIndex) => {
    setAnswers((prev) => ({ ...prev, [qid]: optionIndex }));
  };

  const nextQuestion = () => {
    if (currentQuestionIdx < questions.length - 1) {
      setCurrentQuestionIdx((p) => p + 1);
    }
  };

  const prevQuestion = () => {
    if (currentQuestionIdx > 0) {
      setCurrentQuestionIdx((p) => p - 1);
    }
  };

  const handleSubmit = async () => {
    try {
      const payload = buildPayload({ profileData, answers });
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch {
      alert("Assessment failed");
    }
  };

  const currentQuestion = questions[currentQuestionIdx];
  const progress = ((currentQuestionIdx + 1) / questions.length) * 100;

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 to-purple-100 dark:from-slate-900 dark:to-black">

      {/* HEADER */}
      <header className="fixed top-0 w-full z-50 backdrop-blur bg-white/70 dark:bg-black/60">
        <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between">
          <h1 className="font-extrabold text-xl">
            CollegeReview<span className="text-indigo-500">Z</span>
          </h1>
          <button onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? "‚òÄÔ∏è" : "üåô"}
          </button>
        </div>
      </header>

      <div className="h-28" />

      {/* PROFILE SECTION */}
      {step === "profile" && (
        <motion.section {...pageFade} className="max-w-4xl mx-auto bg-white/80 dark:bg-slate-900/70 p-8 rounded-3xl shadow-2xl">
          <h2 className="text-xl font-semibold mb-6">üéì Student Details</h2>

          <div className="grid md:grid-cols-2 gap-5">
            <input name="name" placeholder="Full Name *" onChange={handleProfileChange} className="p-4 rounded-xl border" />
            <input name="phone" placeholder="Phone Number *" onChange={handleProfileChange} className="p-4 rounded-xl border" />

            <select name="educationLevel" onChange={handleProfileChange} className="p-4 rounded-xl border">
              <option value="">Education Level *</option>
              {["9th","10th","11th","12th","Graduation"].map(v => <option key={v}>{v}</option>)}
            </select>

            <select name="educationStatus" onChange={handleProfileChange} className="p-4 rounded-xl border">
              <option value="">Status *</option>
              <option>Pursuing</option>
              <option>Completed</option>
            </select>

            {(profileData.educationLevel === "11th" || profileData.educationLevel === "12th") && (
              <select name="field" onChange={handleProfileChange} className="p-4 rounded-xl border">
                <option value="">Field *</option>
                {["Science","Arts","Commerce"].map(v => <option key={v}>{v}</option>)}
              </select>
            )}

            {profileData.educationLevel === "Graduation" && (
              <select name="field" onChange={handleProfileChange} className="p-4 rounded-xl border">
                <option value="">Graduation Field *</option>
                {["BA","BSc","BCom","BTech","BCA","LAW","MBBS"].map(v => <option key={v}>{v}</option>)}
              </select>
            )}
          </div>

          {/* MARKS */}
          <div className="grid md:grid-cols-3 gap-4 mt-6">
            {showMark("class9") && <input placeholder="9th %" onChange={e => handleMarksChange("class9", e.target.value)} className="p-3 rounded-xl border" />}
            {showMark("class10") && <input placeholder="10th %" onChange={e => handleMarksChange("class10", e.target.value)} className="p-3 rounded-xl border" />}
            {showMark("class11") && <input placeholder="11th %" onChange={e => handleMarksChange("class11", e.target.value)} className="p-3 rounded-xl border" />}
            {showMark("class12") && <input placeholder="12th %" onChange={e => handleMarksChange("class12", e.target.value)} className="p-3 rounded-xl border" />}
            {showMark("graduation") && <input placeholder="Graduation %" onChange={e => handleMarksChange("graduation", e.target.value)} className="p-3 rounded-xl border" />}
          </div>

          <div className="flex justify-end mt-8">
            <button disabled={!isProfileComplete} onClick={() => setStep("questions")}
              className="px-8 py-3 rounded-xl bg-indigo-500 text-white disabled:opacity-40">
              Save & Continue ‚Üí
            </button>
          </div>
        </motion.section>
      )}

      {/* QUESTIONS SECTION (UNCHANGED) */}
      {step === "questions" && (
        <div className="max-w-4xl mx-auto px-4 pb-24">
          <div className="h-2 bg-white/40 rounded-full mb-8">
            <motion.div className="h-full bg-indigo-500" animate={{ width: `${progress}%` }} />
          </div>

          <motion.div key={currentQuestion.id} {...pageFade}
            className="bg-white/80 dark:bg-slate-900/70 p-8 rounded-3xl shadow-xl">

            <h3 className="text-lg font-semibold mb-6">{currentQuestion.text}</h3>

            {currentQuestion.options.map((opt, idx) => (
              <label key={idx} className="block p-4 border rounded-xl mb-3 cursor-pointer">
                <input type="radio" className="hidden"
                  checked={answers[currentQuestion.id] === idx}
                  onChange={() => handleSelect(currentQuestion.id, idx)} />
                {opt}
              </label>
            ))}

            <div className="flex justify-between mt-8">
              <button onClick={prevQuestion} disabled={currentQuestionIdx === 0}>Previous</button>
              {currentQuestionIdx < questions.length - 1 ? (
                <button onClick={nextQuestion}>Save & Continue</button>
              ) : (
                <button onClick={handleSubmit}>Submit</button>
              )}
            </div>
          </motion.div>
        </div>
      )}
    </div>
  );
};

export default AssessmentPage;
