import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import questions from "../data/questions";
import { runAssessment } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import { buildPayload } from "../utils/payloadBuilder";

const fadeBounce = {
  initial: { opacity: 0, y: 30, scale: 0.95 },
  animate: { opacity: 1, y: 0, scale: 1, transition: { type: "spring", stiffness: 120 } },
  exit: { opacity: 0, y: -30, scale: 0.95, transition: { duration: 0.3 } }
};

const buttonBounce = {
  whileHover: { scale: 1.1 },
  whileTap: { scale: 0.95 }
};

const AssessmentPage = () => {
  const navigate = useNavigate();
  const [darkMode, setDarkMode] = useState(false);
  const [step, setStep] = useState("profile");

  /** PROFILE DATA */
  const [profileData, setProfileData] = useState({
    age: "",
    currentClass: "",
    stream: "",
    familyAnnualBudget: "",
    educationLoanComfort: "",
    coachingAffordability: false,
  });

  const [answers, setAnswers] = useState({});
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);

  /** DARK MODE TOGGLE */
  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  /** PROFILE HANDLER */
  const handleProfileChange = (e) => {
    const { name, value, type, checked } = e.target;
    setProfileData((p) => ({
      ...p,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const isProfileComplete = Object.entries(profileData).every(
    ([key, value]) =>
      key === "coachingAffordability"
        ? typeof value === "boolean"
        : value !== ""
  );

  /** ANSWER SELECT */
  const handleSelect = (qid, optionIndex) => {
    setAnswers((prev) => ({
      ...prev,
      [qid]: optionIndex,
    }));
  };

  /** SUBMIT */
  const handleSubmit = async () => {
    if (!isProfileComplete) {
      alert("Please complete profile");
      return;
    }
    if (Object.keys(answers).length !== questions.length) {
      alert("Please answer all questions");
      return;
    }
    try {
      const payload = buildPayload({ profileData, answers });
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch (err) {
      console.error("ASSESSMENT ERROR:", err.response?.data || err.message);
      alert("Assessment failed");
    }
  };

  const currentQuestion = questions[currentQuestionIdx];
  const progress = ((currentQuestionIdx + 1) / questions.length) * 100;

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-sky-100 to-purple-100 dark:from-slate-900 dark:via-slate-950 dark:to-black transition-all duration-700">

      {/* HEADER */}
      <header className="fixed top-0 w-full z-50 backdrop-blur-lg bg-white/80 dark:bg-black/70 border-b border-white/20">
        <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="font-extrabold text-2xl tracking-wide">
            CollegeReview<span className="text-indigo-500">Z</span>
          </h1>
          <motion.button {...buttonBounce} onClick={() => setDarkMode(!darkMode)}
            className="w-12 h-12 rounded-full bg-black/10 dark:bg-white/10 flex items-center justify-center text-xl">
            {darkMode ? "‚òÄÔ∏è" : "üåô"}
          </motion.button>
        </div>
      </header>

      <div className="h-28" />

      {/* PROFILE SECTION */}
      {step === "profile" && (
        <AnimatePresence>
          <motion.section {...fadeBounce} key="profile"
            className="max-w-4xl mx-auto bg-white/90 dark:bg-slate-900/80 backdrop-blur-xl rounded-3xl shadow-2xl p-10">

            <h2 className="text-2xl font-bold mb-8 text-center">üéì Student Profile</h2>

            <div className="grid md:grid-cols-2 gap-6">

              <motion.input {...buttonBounce} name="age" type="number" placeholder="Age"
                onChange={handleProfileChange}
                className="p-4 rounded-2xl border-2 border-indigo-300 dark:border-indigo-600 bg-white dark:bg-slate-800 focus:ring-2 focus:ring-indigo-500"
              />

              <motion.select {...buttonBounce} name="currentClass" onChange={handleProfileChange}
                className="p-4 rounded-2xl border-2 border-indigo-300 dark:border-indigo-600 bg-white dark:bg-slate-800">
                <option value="">Current Class</option>
                <option value="Class 9">Class 9</option>
                <option value="Class 10">Class 10</option>
                <option value="Class 11">Class 11</option>
                <option value="Class 12">Class 12</option>
                <option value="Dropper">Dropper</option>
                <option value="Undergraduate">Undergraduate</option>
              </motion.select>

              <motion.select {...buttonBounce} name="stream" onChange={handleProfileChange}
                className="p-4 rounded-2xl border-2 border-indigo-300 dark:border-indigo-600 bg-white dark:bg-slate-800">
                <option value="">Stream</option>
                <option value="Science">Science</option>
                <option value="Commerce">Commerce</option>
                <option value="Arts">Arts</option>
                <option value="Undecided">Undecided</option>
              </motion.select>

              <motion.select {...buttonBounce} name="familyAnnualBudget" onChange={handleProfileChange}
                className="p-4 rounded-2xl border-2 border-indigo-300 dark:border-indigo-600 bg-white dark:bg-slate-800">
                <option value="">Family Annual Budget</option>
                <option value="< 1 Lakh">&lt; 1 Lakh</option>
                <option value="1‚Äì3 Lakh">1‚Äì3 Lakh</option>
                <option value="3‚Äì6 Lakh">3‚Äì6 Lakh</option>
                <option value="6‚Äì10 Lakh">6‚Äì10 Lakh</option>
                <option value="> 10 Lakh">&gt; 10 Lakh</option>
              </motion.select>

              <motion.select {...buttonBounce} name="educationLoanComfort" onChange={handleProfileChange}
                className="p-4 rounded-2xl border-2 border-indigo-300 dark:border-indigo-600 bg-white dark:bg-slate-800">
                <option value="">Education Loan Comfort</option>
                <option value="No">No</option>
                <option value="Maybe">Maybe</option>
                <option value="Yes">Yes</option>
              </motion.select>

              <motion.label {...buttonBounce} className="flex items-center gap-3 p-4 border-2 border-indigo-300 dark:border-indigo-600 rounded-2xl bg-white dark:bg-slate-800 cursor-pointer">
                <input type="checkbox" name="coachingAffordability" onChange={handleProfileChange} />
                Can afford coaching?
              </motion.label>
            </div>

            <div className="flex justify-center mt-10">
              <motion.button {...buttonBounce}
                disabled={!isProfileComplete}
                onClick={() => setStep("questions")}
                className="px-10 py-4 bg-indigo-500 text-white font-bold text-lg rounded-2xl disabled:opacity-50 shadow-xl">
                Save & Continue ‚Üí
              </motion.button>
            </div>
          </motion.section>
        </AnimatePresence>
      )}

      /{/* QUESTIONS SECTION */}
      {step === "questions" && (
        <div className="max-w-4xl mx-auto px-4 pb-24">
          {/* Progress Bar */}
          <div className="h-3 bg-white/40 rounded-full mb-8 overflow-hidden">
            <motion.div
              className="h-full bg-gradient-to-r from-indigo-500 to-purple-500"
              animate={{ width: `${progress}%` }}
              transition={{ type: "spring", stiffness: 80 }}
            />
          </div>

          <AnimatePresence mode="wait">
            <motion.div key={currentQuestion.id} {...fadeBounce}
              className="bg-white dark:bg-slate-900 p-8 rounded-3xl shadow-2xl border-4 border-indigo-300 dark:border-indigo-600">

              <p className="text-sm opacity-70 mb-2">
                Question {currentQuestionIdx + 1} of {questions.length}
              </p>

              <h3 className="text-xl font-bold mb-6">{currentQuestion.text}</h3>

              <div className="space-y-4">
                {currentQuestion.options.map((opt, idx) => {
                  const active = answers[currentQuestion.id] === idx;
                  return (
                    <motion.label {...buttonBounce} key={idx}
                      className={`flex p-4 rounded-2xl border-2 cursor-pointer
                      ${active ? "bg-indigo-500 text-white border-indigo-700" : "bg-white dark:bg-slate-800 border-indigo-300 dark:border-indigo-600"}`}>
                      <input
                        type="radio"
                        checked={active}
                        onChange={() => handleSelect(currentQuestion.id, idx)}
                        className="hidden"
                      />
                      {typeof opt === "string" ? opt : opt.label}
                    </motion.label>
                  );
                })}
              </div>

              {/* Controls */}
              <div className="flex justify-between mt-10">
                <motion.button {...buttonBounce}
                  onClick={() => setCurrentQuestionIdx((p) => p - 1)}
                  disabled={currentQuestionIdx === 0}
                  className="px-6 py-3 bg-slate-200 dark:bg-slate-700 rounded-xl font-bold">
                  Previous
                </motion.button>

                {currentQuestionIdx < questions.length - 1 ? (
                  <motion.button {...buttonBounce}
                    onClick={() => setCurrentQuestionIdx((p) => p + 1)}
                    className="px-6 py-3 bg-indigo-500 text-white rounded-xl font-bold">
                    Next ‚Üí
                  </motion.button>
                ) : (
                  <motion.button {...buttonBounce}
                    onClick={handleSubmit}
                    className="px-6 py-3 bg-green-500 text-white rounded-xl font-bold">
                    Submit Assessment
                  </motion.button>
                )}
              </div>
            </motion.div>
          </AnimatePresence>
        </div>
      )}

      <footer className="text-center py-6 text-sm opacity-60">
        ¬© 2026 @ CollegeReviewZ
      </footer>
    </div>
  );
};

export default AssessmentPage;
