// src/pages/AssessmentPage.jsx

import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import questions from "../data/questions";
import { runAssessment } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import ProfileForm from "../components/ProfileForm";
import { buildPayload } from "../utils/payloadBuilder";
import { SunIcon, MoonIcon, EnvelopeIcon } from "@heroicons/react/24/outline";
import logoImg from "../assets/Co"; // Your CollegeReviewZ logo
import websiteLogo from "../assets/web-logo.png"; // Small website logo

const AssessmentPage = () => {
  const navigate = useNavigate();

  const [darkMode, setDarkMode] = useState(false);
  const [profileData, setProfileData] = useState({
    age: "",
    currentClass: "",
    stream: "",
    familyAnnualBudget: "",
    educationLoanComfort: "",
    coachingAffordability: false,
  });
  const [answers, setAnswers] = useState({});
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);

  // Dark mode toggle effect
  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  const handleSelect = (qid, optionIndex) => {
    setAnswers((prev) => ({
      ...prev,
      [qid]: optionIndex,
    }));
  };

  const isProfileComplete = Object.values(profileData).every(
    (v) => v !== "" && v !== null && v !== undefined
  );

  const handleNext = () => {
    if (currentQuestionIdx < questions.length - 1) {
      setCurrentQuestionIdx((prev) => prev + 1);
    }
  };

  const handlePrev = () => {
    if (currentQuestionIdx > 0) {
      setCurrentQuestionIdx((prev) => prev - 1);
    }
  };

  const handleSkip = () => {
    handleNext();
  };

  const handleSubmit = async () => {
    if (!isProfileComplete) {
      alert("Please complete profile before assessment");
      return;
    }

    if (Object.keys(answers).length !== questions.length) {
      alert("Please answer all questions");
      return;
    }

    const payload = buildPayload({ profileData, answers });

    try {
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch (err) {
      console.error("ASSESSMENT ERROR:", err.response?.data || err.message);
      alert("Assessment failed — check console");
    }
  };

  const currentQuestion = questions[currentQuestionIdx];

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100 transition-colors duration-500">
      {/* Header */}
      <header className="flex items-center justify-between p-4 shadow-md bg-white dark:bg-gray-800 fixed w-full top-0 z-50">
        <div className="flex items-center space-x-3">
          {/* Dark Mode Toggle */}
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="p-2 rounded-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 transition"
          >
            {darkMode ? (
              <SunIcon className="h-5 w-5 text-yellow-400" />
            ) : (
              <MoonIcon className="h-5 w-5 text-gray-900" />
            )}
          </button>

          {/* Logo + Name */}
          <img src={logoImg} alt="CollegeReviewZ Logo" className="h-8 w-8" />
          <span className="font-bold text-lg">CollegeReviewZ</span>
        </div>

        {/* Contact */}
        <div className="flex flex-col items-end text-right text-sm">
          <div className="flex items-center space-x-1">
            <EnvelopeIcon className="h-4 w-4" />
            <span>admin@collegereviewz.com</span>
          </div>
          <div className="flex items-center space-x-1 mt-1">
            <img src={websiteLogo} alt="Website Logo" className="h-4 w-4" />
            <span>collegereviewz.com</span>
          </div>
        </div>
      </header>

      {/* Spacer */}
      <div className="h-28" />

      {/* Profile Form */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="max-w-3xl mx-auto p-6 bg-white dark:bg-gray-800 shadow-lg rounded-lg mb-6"
      >
        <ProfileForm profileData={profileData} setProfileData={setProfileData} />
      </motion.div>

      {/* Question Section */}
      <div className="max-w-3xl mx-auto p-6">
        <h2 className="text-xl font-bold mb-4">Assessment Questions</h2>

        <AnimatePresence exitBeforeEnter>
          <motion.div
            key={currentQuestion.id}
            initial={{ x: 100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: -100, opacity: 0 }}
            transition={{ type: "spring", stiffness: 300, damping: 30 }}
            className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md mb-4"
          >
            <p className="font-semibold mb-4">
              {currentQuestion.id}. {currentQuestion.text}
            </p>

            <div className="space-y-2">
              {currentQuestion.options.map((opt, idx) => {
                const label = typeof opt === "string" ? opt : opt.label;
                return (
                  <label key={idx} className="flex items-center space-x-2">
                    <input
                      type="radio"
                      name={currentQuestion.id}
                      checked={answers[currentQuestion.id] === idx}
                      onChange={() => handleSelect(currentQuestion.id, idx)}
                      className="accent-blue-500"
                    />
                    <span>{label}</span>
                  </label>
                );
              })}
            </div>

            {/* Navigation Buttons */}
            <div className="flex justify-between mt-6">
              <button
                onClick={handlePrev}
                disabled={currentQuestionIdx === 0}
                className="px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded hover:bg-gray-300 dark:hover:bg-gray-600 transition disabled:opacity-50"
              >
                Previous
              </button>

              <button
                onClick={handleSkip}
                className="px-4 py-2 bg-yellow-400 text-gray-900 rounded hover:bg-yellow-500 transition"
              >
                Skip
              </button>

              {currentQuestionIdx < questions.length - 1 ? (
                <button
                  onClick={handleNext}
                  className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
                >
                  Save & Continue
                </button>
              ) : (
                <button
                  onClick={handleSubmit}
                  className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 transition"
                >
                  Submit Assessment
                </button>
              )}
            </div>
          </motion.div>
        </AnimatePresence>
      </div>

      {/* Footer */}
      <footer className="flex justify-between items-center p-4 bg-white dark:bg-gray-800 mt-10 shadow-inner">
        <span>2026@collegereviewz.com</span>
        <a href="tel:+918768506379" className="text-blue-500 hover:underline">
          +91 87685 06379
        </a>
      </footer>
    </div>
  );
};

export default AssessmentPage;
