import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import questions from "../data/questions";
import { runAssessment } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import { buildPayload } from "../utils/payloadBuilder";

const pageFade = {
  initial: { opacity: 0, y: 40 },
  animate: { opacity: 1, y: 0 },
  exit: { opacity: 0, y: -40 },
};

const AssessmentPage = () => {
  const navigate = useNavigate();
  const [darkMode, setDarkMode] = useState(false);

  /** STEP CONTROL */
  const [step, setStep] = useState("profile");

  /** üîÅ OLD PROFILE DATA (RESTORED) */
  const [profileData, setProfileData] = useState({
    age: "",
    currentClass: "",
    stream: "",
    familyAnnualBudget: "",
    educationLoanComfort: "",
    coachingAffordability: false,
  });

  /** QUESTIONS STATE */
  const [answers, setAnswers] = useState({});
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  /** PROFILE HANDLER */
  const handleProfileChange = (e) => {
    const { name, value, type, checked } = e.target;
    setProfileData((p) => ({
      ...p,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const isProfileComplete = Object.values(profileData).every(
    (v) => v !== "" && v !== null && v !== undefined
  );

  /** ANSWER SELECT */
  const handleSelect = (qid, optionIndex) => {
    setAnswers((prev) => ({
      ...prev,
      [qid]: optionIndex,
    }));
  };

  /** NAVIGATION */
  const nextQuestion = () => {
    if (currentQuestionIdx < questions.length - 1) {
      setCurrentQuestionIdx((p) => p + 1);
    }
  };

  const prevQuestion = () => {
    if (currentQuestionIdx > 0) {
      setCurrentQuestionIdx((p) => p - 1);
    }
  };

  /** FINAL SUBMIT */
  const handleSubmit = async () => {
    try {
      const payload = buildPayload({ profileData, answers });
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch (err) {
      console.error(err);
      alert("Assessment failed");
    }
  };

  const currentQuestion = questions[currentQuestionIdx];
  const progress = ((currentQuestionIdx + 1) / questions.length) * 100;

  return (
    <div className="min-h-screen relative overflow-hidden bg-gradient-to-br from-indigo-100 via-sky-100 to-purple-100 dark:from-slate-900 dark:via-slate-950 dark:to-black transition-all duration-500">

      {/* HEADER */}
      <header className="fixed top-0 w-full z-50 backdrop-blur bg-white/70 dark:bg-black/60 border-b border-white/20">
        <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="font-extrabold text-xl tracking-wide">
            CollegeReview<span className="text-indigo-500">Z</span>
          </h1>

          <button
            onClick={() => setDarkMode(!darkMode)}
            className="w-10 h-10 rounded-full bg-black/10 dark:bg-white/10 flex items-center justify-center text-lg hover:scale-110 transition"
          >
            {darkMode ? "‚òÄÔ∏è" : "üåô"}
          </button>
        </div>
      </header>

      <div className="h-28" />

      {/* PROFILE SECTION */}
      {step === "profile" && (
        <motion.section {...pageFade}
          className="max-w-4xl mx-auto bg-white/80 dark:bg-slate-900/70 backdrop-blur-xl rounded-3xl shadow-2xl p-8 mb-12">

          <h2 className="text-xl font-semibold mb-6">üéì Student Profile</h2>

          <div className="grid md:grid-cols-2 gap-5">

            <input
              name="age"
              placeholder="Age"
              onChange={handleProfileChange}
              className="p-4 rounded-xl bg-white dark:bg-slate-800 border"
            />

            <input
              name="currentClass"
              placeholder="Current Class"
              onChange={handleProfileChange}
              className="p-4 rounded-xl bg-white dark:bg-slate-800 border"
            />

            <select
              name="stream"
              onChange={handleProfileChange}
              className="p-4 rounded-xl bg-white dark:bg-slate-800 border"
            >
              <option value="">Stream *</option>
              <option>Science</option>
              <option>Arts</option>
              <option>Commerce</option>
            </select>

            <select
              name="familyAnnualBudget"
              onChange={handleProfileChange}
              className="p-4 rounded-xl bg-white dark:bg-slate-800 border"
            >
              <option value="">Family Annual Budget</option>
              <option>4 Lakh</option>
            </select>

            <select
              name="educationLoanComfort"
              onChange={handleProfileChange}
              className="p-4 rounded-xl bg-white dark:bg-slate-800 border"
            >
              <option value="">Comfort with Education Loan?</option>
              <option>Yes</option>
              <option>No</option>
            </select>

            <label className="flex items-center gap-3 p-4 rounded-xl bg-white dark:bg-slate-800 border">
              <input
                type="checkbox"
                name="coachingAffordability"
                onChange={handleProfileChange}
              />
              Can afford coaching?
            </label>
          </div>

          <div className="flex justify-end mt-8">
            <button
              disabled={!isProfileComplete}
              onClick={() => setStep("questions")}
              className="px-8 py-3 rounded-xl bg-indigo-500 text-white disabled:opacity-40"
            >
              Save & Continue ‚Üí
            </button>
          </div>
        </motion.section>
      )}

      {/* QUESTIONS SECTION */}
      {step === "questions" && (
        <div className="max-w-4xl mx-auto px-4 pb-24">

          <div className="h-2 bg-white/40 rounded-full mb-8">
            <motion.div
              className="h-full bg-gradient-to-r from-indigo-500 to-purple-500"
              animate={{ width: `${progress}%` }}
            />
          </div>

          <AnimatePresence mode="wait">
            <motion.div key={currentQuestion.id} {...pageFade}
              className="bg-white/80 dark:bg-slate-900/70 rounded-3xl shadow-xl p-8">

              <p className="text-sm opacity-70 mb-2">
                Question {currentQuestionIdx + 1} of {questions.length}
              </p>

              <h3 className="text-lg font-semibold mb-6">
                {currentQuestion.text}
              </h3>

              <div className="space-y-4">
                {currentQuestion.options.map((opt, idx) => {
                  const active = answers[currentQuestion.id] === idx;
                  return (
                    <motion.label key={idx}
                      whileHover={{ scale: 1.02 }}
                      className={`flex p-4 rounded-2xl cursor-pointer border
                      ${active ? "bg-indigo-500 text-white" : "bg-white dark:bg-slate-800"}`}
                    >
                      <input
                        type="radio"
                        checked={active}
                        onChange={() => handleSelect(currentQuestion.id, idx)}
                        className="hidden"
                      />
                      {typeof opt === "string" ? opt : opt.label}
                    </motion.label>
                  );
                })}
              </div>

              <div className="flex justify-between mt-10">
                <button
                  onClick={prevQuestion}
                  disabled={currentQuestionIdx === 0}
                  className="px-6 py-3 rounded-xl bg-slate-200 dark:bg-slate-700"
                >
                  Previous
                </button>

                {currentQuestionIdx < questions.length - 1 ? (
                  <button
                    onClick={nextQuestion}
                    className="px-6 py-3 rounded-xl bg-indigo-500 text-white"
                  >
                    Save & Continue ‚Üí
                  </button>
                ) : (
                  <button
                    onClick={handleSubmit}
                    className="px-6 py-3 rounded-xl bg-green-500 text-white"
                  >
                    Submit Assessment
                  </button>
                )}
              </div>
            </motion.div>
          </AnimatePresence>
        </div>
      )}

      <footer className="text-center py-6 text-sm opacity-60">
        ¬© 2026 @ CollegeReviewZ
      </footer>
    </div>
  );
};

export default AssessmentPage;
