import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import questions from "../data/questions";
import { runAssessment } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import { buildPayload } from "../utils/payloadBuilder";

const pageFade = {
  initial: { opacity: 0, y: 40 },
  animate: { opacity: 1, y: 0 },
  exit: { opacity: 0, y: -40 },
};

const AssessmentPage = () => {
  const navigate = useNavigate();
  const [darkMode, setDarkMode] = useState(false);

  /** STEP CONTROL */
  const [step, setStep] = useState("profile");

  /** PROFILE DATA (EXTENDED, NOTHING REMOVED) */
  const [profileData, setProfileData] = useState({
    name: "",
    phone: "",
    educationClass: "",
    educationStatus: "",
    field: "",
    marks: {},
    stream: "",
    board: "",
    state: "",
    language: "",
    budget: "",
    abroad: "",
  });

  /** QUESTIONS STATE */
  const [answers, setAnswers] = useState({});
  const [skipped, setSkipped] = useState({});
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  /** PROFILE HANDLER */
  const handleProfileChange = (e) => {
    setProfileData((p) => ({ ...p, [e.target.name]: e.target.value }));
  };

  const handleMarksChange = (key, value) => {
    setProfileData((p) => ({
      ...p,
      marks: { ...p.marks, [key]: value },
    }));
  };

  /** MARKS LOGIC (AS PER RULES) */
  const getMarksFields = () => {
    const { educationClass, educationStatus } = profileData;
    const fields = [];

    if (educationClass === "9th" && educationStatus === "Completed") {
      fields.push("9th");
    }

    if (educationClass === "10th") {
      fields.push("9th");
      if (educationStatus === "Completed") fields.push("10th");
    }

    if (educationClass === "11th") {
      fields.push("9th", "10th");
      if (educationStatus === "Completed") fields.push("11th");
    }

    if (educationClass === "12th") {
      fields.push("10th", "11th");
      if (educationStatus === "Completed") fields.push("12th");
    }

    if (educationClass === "Graduation") {
      fields.push("10th", "12th");
      if (educationStatus === "Completed") fields.push("Graduation");
    }

    return fields;
  };

  const isProfileComplete =
    profileData.name &&
    profileData.phone &&
    profileData.educationClass &&
    profileData.educationStatus &&
    profileData.stream &&
    profileData.board &&
    profileData.language &&
    profileData.budget &&
    profileData.abroad &&
    getMarksFields().every((m) => profileData.marks[m]);

  /** ANSWER SELECT (AUTO SAVE) */
  const handleSelect = (qid, optionIndex) => {
    setAnswers((prev) => ({
      ...prev,
      [qid]: optionIndex,
    }));
  };

  /** NAVIGATION */
  const nextQuestion = () => {
    if (currentQuestionIdx < questions.length - 1) {
      setCurrentQuestionIdx((p) => p + 1);
    }
  };

  const prevQuestion = () => {
    if (currentQuestionIdx > 0) {
      setCurrentQuestionIdx((p) => p - 1);
    }
  };

  /** FINAL SUBMIT */
  const handleSubmit = async () => {
    try {
      const payload = buildPayload({ profileData, answers });
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch {
      alert("Assessment failed");
    }
  };

  const currentQuestion = questions[currentQuestionIdx];
  const progress = ((currentQuestionIdx + 1) / questions.length) * 100;

  return (
    <div className="min-h-screen relative overflow-hidden bg-gradient-to-br from-indigo-100 via-sky-100 to-purple-100 dark:from-slate-900 dark:via-slate-950 dark:to-black transition-all duration-500">

      {/* HEADER */}
      <header className="fixed top-0 w-full z-50 backdrop-blur bg-white/70 dark:bg-black/60 border-b border-white/20">
        <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="font-extrabold text-xl tracking-wide">
            CollegeReview<span className="text-indigo-500">Z</span>
          </h1>

          <button
            onClick={() => setDarkMode(!darkMode)}
            className="w-10 h-10 rounded-full bg-black/10 dark:bg-white/10 flex items-center justify-center text-lg"
          >
            {darkMode ? "‚òÄÔ∏è" : "üåô"}
          </button>
        </div>
      </header>

      <div className="h-28" />

      {/* PROFILE SECTION (UPDATED ONLY) */}
      {step === "profile" && (
        <motion.section {...pageFade} className="max-w-4xl mx-auto bg-white/80 dark:bg-slate-900/70 rounded-3xl shadow-2xl p-8 mb-12">
          <h2 className="text-xl font-semibold mb-6">üéì Student Details</h2>

          <div className="grid md:grid-cols-2 gap-5">
            <input name="name" placeholder="Full Name *" onChange={handleProfileChange} className="p-4 rounded-xl border" />
            <input name="phone" placeholder="Phone No *" onChange={handleProfileChange} className="p-4 rounded-xl border" />

            <select name="educationClass" onChange={handleProfileChange} className="p-4 rounded-xl border">
              <option value="">Education *</option>
              {["9th","10th","11th","12th","Graduation"].map(v => <option key={v}>{v}</option>)}
            </select>

            <select name="educationStatus" onChange={handleProfileChange} className="p-4 rounded-xl border">
              <option value="">Status *</option>
              <option>Pursuing</option>
              <option>Completed</option>
            </select>

            {(profileData.educationClass === "11th" ||
              profileData.educationClass === "12th" ||
              profileData.educationClass === "Graduation") && (
              <select name="field" onChange={handleProfileChange} className="p-4 rounded-xl border">
                <option value="">Field *</option>
                {(profileData.educationClass === "Graduation"
                  ? ["BA","BSc","BCom","BTech","BCA","LAW","MBBS"]
                  : ["Science","Arts","Commerce"]
                ).map(f => <option key={f}>{f}</option>)}
              </select>
            )}

            {getMarksFields().map((m) => (
              <input
                key={m}
                placeholder={`${m} Marks (%) *`}
                onChange={(e) => handleMarksChange(m, e.target.value)}
                className="p-4 rounded-xl border"
              />
            ))}

            <input name="stream" placeholder="Stream *" onChange={handleProfileChange} className="p-4 rounded-xl border" />
            <input name="board" placeholder="Board *" onChange={handleProfileChange} className="p-4 rounded-xl border" />

            <select name="language" onChange={handleProfileChange} className="p-4 rounded-xl border">
              <option value="">Language *</option>
              {["Assamese","Bengali","Bodo","Dogri","Gujarati","Hindi","Kannada","Kashmiri","Konkani","Maithili","Malayalam","Manipuri","Marathi","Nepali","Odia","Punjabi","Sanskrit","Santali","Sindhi","Tamil","Telugu","Urdu"]
                .map(l => <option key={l}>{l}</option>)}
            </select>

            <select name="budget" onChange={handleProfileChange} className="p-4 rounded-xl border">
              <option value="">Budget *</option>
              {["5-6","6-7","7-10","10-15","15-20","20-30"].map(b => <option key={b}>{b} Lakhs</option>)}
            </select>

            <select name="abroad" onChange={handleProfileChange} className="p-4 rounded-xl border">
              <option value="">Open To Abroad *</option>
              <option>No</option>
              <option>Yes</option>
            </select>
          </div>

          <div className="flex justify-end mt-8">
            <button
              disabled={!isProfileComplete}
              onClick={() => setStep("questions")}
              className="px-8 py-3 rounded-xl bg-indigo-500 text-white disabled:opacity-40"
            >
              Save & Continue ‚Üí
            </button>
          </div>
        </motion.section>
      )}

      {/* QUESTIONS SECTION (UNCHANGED) */}
      {step === "questions" && (
        <div className="max-w-4xl mx-auto px-4 pb-24">
          <div className="h-2 bg-white/40 rounded-full mb-8">
            <motion.div className="h-full bg-gradient-to-r from-indigo-500 to-purple-500" animate={{ width: `${progress}%` }} />
          </div>

          <AnimatePresence mode="wait">
            <motion.div key={currentQuestion.id} {...pageFade} className="bg-white/80 dark:bg-slate-900/70 rounded-3xl shadow-xl p-8">
              <p className="text-sm opacity-70 mb-2">
                Question {currentQuestionIdx + 1} of {questions.length}
              </p>

              <h3 className="text-lg font-semibold mb-6">{currentQuestion.text}</h3>

              <div className="space-y-4">
                {currentQuestion.options.map((opt, idx) => {
                  const active = answers[currentQuestion.id] === idx;
                  return (
                    <motion.label key={idx} whileHover={{ scale: 1.02 }}
                      className={`flex p-4 rounded-2xl cursor-pointer border ${active ? "bg-indigo-500 text-white" : "bg-white dark:bg-slate-800"}`}>
                      <input type="radio" checked={active} onChange={() => handleSelect(currentQuestion.id, idx)} className="hidden" />
                      {typeof opt === "string" ? opt : opt.label}
                    </motion.label>
                  );
                })}
              </div>

              <div className="flex justify-between mt-10">
                <button onClick={prevQuestion} disabled={currentQuestionIdx === 0} className="px-6 py-3 rounded-xl bg-slate-200 dark:bg-slate-700">
                  Previous
                </button>

                {currentQuestionIdx < questions.length - 1 ? (
                  <button onClick={nextQuestion} className="px-6 py-3 rounded-xl bg-indigo-500 text-white">
                    Save & Continue ‚Üí
                  </button>
                ) : (
                  <button onClick={handleSubmit} className="px-6 py-3 rounded-xl bg-green-500 text-white">
                    Submit Assessment
                  </button>
                )}
              </div>
            </motion.div>
          </AnimatePresence>
        </div>
      )}

      <footer className="text-center py-6 text-sm opacity-60">¬© 2026 CollegeReviewZ</footer>
    </div>
  );
};

export default AssessmentPage;
