// src/pages/AssessmentPage.jsx

import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import questions from "../data/questions";
import { runAssessment } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import ProfileForm from "../components/ProfileForm";
import { buildPayload } from "../utils/payloadBuilder";

const AssessmentPage = () => {
  const navigate = useNavigate();

  const [darkMode, setDarkMode] = useState(false);
  const [profileData, setProfileData] = useState({
    age: "",
    currentClass: "",
    stream: "",
    familyAnnualBudget: "",
    educationLoanComfort: "",
    coachingAffordability: false,
  });

  const [answers, setAnswers] = useState({});
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  const handleSelect = (qid, optionIndex) => {
    setAnswers((prev) => ({
      ...prev,
      [qid]: optionIndex,
    }));
  };

  const handleNext = () => {
    if (currentQuestionIdx < questions.length - 1) {
      setCurrentQuestionIdx((p) => p + 1);
    }
  };

  const handlePrev = () => {
    if (currentQuestionIdx > 0) {
      setCurrentQuestionIdx((p) => p - 1);
    }
  };

  const handleSubmit = async () => {
    const payload = buildPayload({ profileData, answers });
    try {
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch (err) {
      alert("Assessment failed");
      console.error(err);
    }
  };

  const currentQuestion = questions[currentQuestionIdx];
  const progress =
    ((currentQuestionIdx + 1) / questions.length) * 100;

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-100 to-slate-200 dark:from-slate-900 dark:to-black text-slate-900 dark:text-slate-100 transition-colors duration-500">
      {/* HEADER */}
      <header className="fixed top-0 w-full z-50 bg-white/80 dark:bg-slate-900/80 backdrop-blur border-b border-slate-200 dark:border-slate-800">
        <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="font-bold text-xl tracking-wide">
            CollegeReviewZ
          </h1>

          <button
            onClick={() => setDarkMode(!darkMode)}
            className="text-xl"
          >
            {darkMode ? "‚òÄÔ∏è" : "üåô"}
          </button>
        </div>
      </header>

      <div className="h-24" />

      {/* PROFILE */}
      <motion.div
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        className="max-w-3xl mx-auto bg-white dark:bg-slate-800 rounded-2xl shadow-xl p-6 mb-8"
      >
        <ProfileForm
          profileData={profileData}
          setProfileData={setProfileData}
        />
      </motion.div>

      {/* QUESTIONS */}
      <div className="max-w-3xl mx-auto px-4 pb-16">
        {/* Progress Bar */}
        <div className="w-full h-2 bg-slate-300 dark:bg-slate-700 rounded-full overflow-hidden mb-6">
          <motion.div
            className="h-full bg-blue-500"
            initial={{ width: 0 }}
            animate={{ width: `${progress}%` }}
            transition={{ duration: 0.4 }}
          />
        </div>

        <AnimatePresence mode="wait">
          <motion.div
            key={currentQuestion.id}
            initial={{ opacity: 0, x: 80 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -80 }}
            transition={{ duration: 0.35 }}
            className="bg-white dark:bg-slate-800 rounded-2xl shadow-lg p-6"
          >
            <h2 className="text-lg font-semibold mb-4">
              Question {currentQuestionIdx + 1} of {questions.length}
            </h2>

            <p className="mb-6 text-base">
              {currentQuestion.text}
            </p>

            <div className="space-y-3">
              {currentQuestion.options.map((opt, idx) => {
                const selected =
                  answers[currentQuestion.id] === idx;

                return (
                  <motion.label
                    key={idx}
                    whileHover={{ scale: 1.02 }}
                    className={`flex items-center gap-3 p-3 rounded-xl border cursor-pointer transition
                      ${
                        selected
                          ? "border-blue-500 bg-blue-50 dark:bg-blue-900/30"
                          : "border-slate-300 dark:border-slate-700"
                      }`}
                  >
                    <input
                      type="radio"
                      checked={selected}
                      onChange={() =>
                        handleSelect(currentQuestion.id, idx)
                      }
                    />
                    <span>
                      {typeof opt === "string" ? opt : opt.label}
                    </span>
                  </motion.label>
                );
              })}
            </div>

            {/* NAVIGATION */}
            <div className="flex justify-between mt-8">
              <button
                onClick={handlePrev}
                disabled={currentQuestionIdx === 0}
                className="px-4 py-2 rounded-lg bg-slate-300 dark:bg-slate-700 disabled:opacity-40"
              >
                Previous
              </button>

              {currentQuestionIdx < questions.length - 1 ? (
                <button
                  onClick={handleNext}
                  className="px-6 py-2 rounded-lg bg-blue-500 text-white hover:bg-blue-600"
                >
                  Save & Continue
                </button>
              ) : (
                <button
                  onClick={handleSubmit}
                  className="px-6 py-2 rounded-lg bg-green-500 text-white hover:bg-green-600"
                >
                  Submit Assessment
                </button>
              )}
            </div>
          </motion.div>
        </AnimatePresence>
      </div>

      {/* FOOTER */}
      <footer className="text-center py-6 text-sm text-slate-600 dark:text-slate-400">
        ¬© 2026 CollegeReviewZ ¬∑ +91 87685 06379
      </footer>
    </div>
  );
};

export default AssessmentPage;
