import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import questions from "../data/questions";
import { runAssessment } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import { buildPayload } from "../utils/payloadBuilder";

const pageFade = {
  initial: { opacity: 0, y: 40 },
  animate: { opacity: 1, y: 0 },
  exit: { opacity: 0, y: -40 },
};

const AssessmentPage = () => {
  const navigate = useNavigate();
  const [darkMode, setDarkMode] = useState(false);

  const [profileData, setProfileData] = useState({
    stream: "",
    board: "",
    state: "",
    language: "",
    budget: "",
    abroad: "",
  });

  const [answers, setAnswers] = useState({});
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  const handleProfileChange = (e) => {
    setProfileData((p) => ({ ...p, [e.target.name]: e.target.value }));
  };

  const handleSelect = (qid, optionIndex) => {
    setAnswers((prev) => ({ ...prev, [qid]: optionIndex }));
  };

  const handleSubmit = async () => {
    if (!Object.values(profileData).every(Boolean)) {
      alert("Please complete all profile fields");
      return;
    }

    try {
      const payload = buildPayload({ profileData, answers });
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch {
      alert("Assessment failed");
    }
  };

  const currentQuestion = questions[currentQuestionIdx];
  const progress = ((currentQuestionIdx + 1) / questions.length) * 100;

  return (
    <div className="min-h-screen relative overflow-hidden bg-gradient-to-br from-indigo-100 via-sky-100 to-purple-100 dark:from-slate-900 dark:via-slate-950 dark:to-black transition-all duration-500">

      {/* Background blobs */}
      <div className="absolute -top-40 -left-40 w-96 h-96 bg-purple-400/30 rounded-full blur-3xl" />
      <div className="absolute top-1/2 -right-40 w-96 h-96 bg-blue-400/30 rounded-full blur-3xl" />

      {/* HEADER */}
      <header className="fixed top-0 w-full z-50 backdrop-blur bg-white/70 dark:bg-black/60 border-b border-white/20">
        <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="font-extrabold text-xl tracking-wide">
            CollegeReview<span className="text-indigo-500">Z</span>
          </h1>

          <button
            onClick={() => setDarkMode(!darkMode)}
            className="w-10 h-10 rounded-full bg-black/10 dark:bg-white/10 flex items-center justify-center text-lg hover:scale-110 transition"
          >
            {darkMode ? "‚òÄÔ∏è" : "üåô"}
          </button>
        </div>
      </header>

      <div className="h-28" />

      {/* PROFILE */}
      <motion.section
        {...pageFade}
        className="max-w-4xl mx-auto bg-white/80 dark:bg-slate-900/70 backdrop-blur-xl rounded-3xl shadow-2xl p-8 mb-12"
      >
        <h2 className="text-xl font-semibold mb-6">üéì Student Profile</h2>

        <div className="grid md:grid-cols-2 gap-5">
          {[
            { name: "stream", type: "select", options: ["Science", "Arts", "Commerce"] },
            { name: "board", type: "input", placeholder: "Board" },
            { name: "state", type: "input", placeholder: "State" },
            { name: "language", type: "select", options: ["Hindi","English","Bengali","Tamil","Telugu","Marathi","Gujarati"] },
            { name: "budget", type: "select", options: ["4‚Äì5 Lakh","5‚Äì6 Lakh","6‚Äì7 Lakh","7‚Äì10 Lakh","10‚Äì15 Lakh"] },
            { name: "abroad", type: "select", options: ["Yes","No"] },
          ].map((f) =>
            f.type === "input" ? (
              <input
                key={f.name}
                name={f.name}
                placeholder={f.placeholder}
                onChange={handleProfileChange}
                className="p-4 rounded-xl bg-white dark:bg-slate-800 border border-slate-200 dark:border-slate-700 focus:ring-2 focus:ring-indigo-500 outline-none"
              />
            ) : (
              <select
                key={f.name}
                name={f.name}
                onChange={handleProfileChange}
                className="p-4 rounded-xl bg-white dark:bg-slate-800 border border-slate-200 dark:border-slate-700 focus:ring-2 focus:ring-indigo-500 outline-none"
              >
                <option value="">{f.name} *</option>
                {f.options.map((o) => (
                  <option key={o}>{o}</option>
                ))}
              </select>
            )
          )}
        </div>
      </motion.section>

      {/* QUESTIONS */}
      <div className="max-w-4xl mx-auto px-4 pb-24">

        {/* Progress Bar */}
        <div className="h-2 bg-white/40 rounded-full overflow-hidden mb-8">
          <motion.div
            className="h-full bg-gradient-to-r from-indigo-500 to-purple-500"
            animate={{ width: `${progress}%` }}
            transition={{ duration: 0.6, ease: "easeOut" }}
          />
        </div>

        <AnimatePresence mode="wait">
          <motion.div
            key={currentQuestion.id}
            {...pageFade}
            className="bg-white/80 dark:bg-slate-900/70 backdrop-blur-xl rounded-3xl shadow-xl p-8"
          >
            <p className="text-sm opacity-70 mb-2">
              Question {currentQuestionIdx + 1} of {questions.length}
            </p>

            <h3 className="text-lg font-semibold mb-6">
              {currentQuestion.text}
            </h3>

            <div className="space-y-4">
              {currentQuestion.options.map((opt, idx) => {
                const active = answers[currentQuestion.id] === idx;

                return (
                  <motion.label
                    key={idx}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    className={`flex gap-4 p-4 rounded-2xl cursor-pointer border transition-all
                      ${active
                        ? "bg-indigo-500 text-white border-indigo-500 shadow-lg"
                        : "bg-white dark:bg-slate-800 border-slate-200 dark:border-slate-700 hover:border-indigo-400"
                      }
                    `}
                  >
                    <input
                      type="radio"
                      checked={active}
                      onChange={() => handleSelect(currentQuestion.id, idx)}
                      className="hidden"
                    />
                    <span className="font-medium">
                      {typeof opt === "string" ? opt : opt.label}
                    </span>
                  </motion.label>
                );
              })}
            </div>

            {/* NAV BUTTONS */}
            <div className="flex justify-between mt-10">
              <button
                disabled={currentQuestionIdx === 0}
                onClick={() => setCurrentQuestionIdx((p) => p - 1)}
                className="px-6 py-3 rounded-xl bg-slate-200 dark:bg-slate-700 disabled:opacity-40"
              >
                Previous
              </button>

              {currentQuestionIdx < questions.length - 1 ? (
                <button
                  onClick={() => setCurrentQuestionIdx((p) => p + 1)}
                  className="px-6 py-3 rounded-xl bg-indigo-500 text-white hover:scale-105 transition"
                >
                  Continue ‚Üí
                </button>
              ) : (
                <button
                  onClick={handleSubmit}
                  className="px-6 py-3 rounded-xl bg-green-500 text-white hover:scale-105 transition"
                >
                  Submit Assessment
                </button>
              )}
            </div>
          </motion.div>
        </AnimatePresence>
      </div>

      <footer className="text-center py-6 text-sm opacity-60">
        ¬© 2026 CollegeReviewZ
      </footer>
    </div>
  );
};

export default AssessmentPage;
