import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import questions from "../data/questions";
import { runAssessment } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import { buildPayload } from "../utils/payloadBuilder";

const pageFade = {
  initial: { opacity: 0, y: 40 },
  animate: { opacity: 1, y: 0 },
  exit: { opacity: 0, y: -40 },
};

const AssessmentPage = () => {
  const navigate = useNavigate();

  /* ---------------- EXISTING STATE (NOT REMOVED) ---------------- */
  const [darkMode, setDarkMode] = useState(false);
  const [answers, setAnswers] = useState({});
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);

  /* ---------------- NEW STEP CONTROL ---------------- */
  const [step, setStep] = useState(1); // 1 = Personal, 2 = Questions

  /* ---------------- UPDATED PROFILE DATA ---------------- */
  const [profileData, setProfileData] = useState({
    name: "",
    phone: "",
    educationLevel: "",
    field: "",
    marks: {},
    stream: "",
    board: "",
    language: "",
    budget: "",
    abroad: "",
  });

  /* ---------------- DARK MODE FIX ---------------- */
  useEffect(() => {
    if (darkMode) document.documentElement.classList.add("dark");
    else document.documentElement.classList.remove("dark");
  }, [darkMode]);

  /* ---------------- EXISTING FUNCTIONS (NOT REMOVED) ---------------- */
  const handleSelect = (qid, optionIndex) => {
    setAnswers((prev) => ({ ...prev, [qid]: optionIndex }));
  };

  const handleSubmit = async () => {
    try {
      const payload = buildPayload({ profileData, answers });
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch {
      alert("Assessment failed");
    }
  };

  const currentQuestion = questions[currentQuestionIdx];
  const progress = ((currentQuestionIdx + 1) / questions.length) * 100;

  /* ---------------- EDUCATION ‚Üí MARKS LOGIC ---------------- */
  const getMarksFields = () => {
    const e = profileData.educationLevel;
    if (!e) return [];

    if (e === "9th pursuing") return [];
    if (e === "9th completed") return ["9th"];

    if (e === "10th pursuing") return ["9th"];
    if (e === "10th completed") return ["9th", "10th"];

    if (e === "11th pursuing") return ["9th", "10th"];
    if (e === "11th completed") return ["9th", "10th", "11th"];

    if (e === "12th pursuing") return ["10th", "11th"];
    if (e === "12th completed") return ["10th", "11th", "12th"];

    if (e === "Graduation pursuing") return ["10th", "12th"];
    if (e === "Graduation completed") return ["10th", "12th", "Graduation"];

    return [];
  };

  return (
    <div className="min-h-screen relative overflow-hidden bg-gradient-to-br from-indigo-100 via-sky-100 to-purple-100 dark:from-slate-900 dark:via-slate-950 dark:to-black transition-all duration-500">

      {/* BACKGROUND BLOBS */}
      <div className="absolute -top-40 -left-40 w-96 h-96 bg-purple-400/30 rounded-full blur-3xl" />
      <div className="absolute top-1/2 -right-40 w-96 h-96 bg-blue-400/30 rounded-full blur-3xl" />

      {/* HEADER */}
      <header className="fixed top-0 w-full z-50 backdrop-blur bg-white/70 dark:bg-black/60 border-b border-white/20">
        <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="font-extrabold text-xl">
            CollegeReview<span className="text-indigo-500">Z</span>
          </h1>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="w-10 h-10 rounded-full bg-black/10 dark:bg-white/10 text-lg"
          >
            {darkMode ? "‚òÄÔ∏è" : "üåô"}
          </button>
        </div>
      </header>

      <div className="h-28" />

      {/* ================= PERSONAL DETAILS PAGE ================= */}
      {step === 1 && (
        <motion.section
          {...pageFade}
          className="max-w-4xl mx-auto p-8 rounded-3xl
          bg-white/80 dark:bg-slate-900/80 backdrop-blur-xl shadow-2xl"
        >
          <h2 className="text-2xl font-bold mb-8 text-center">
            üë§ Personal Details
          </h2>

          <div className="grid md:grid-cols-2 gap-5">
            <input
              placeholder="Name *"
              className="input"
              onChange={(e) => setProfileData({ ...profileData, name: e.target.value })}
            />
            <input
              placeholder="Phone No *"
              className="input"
              onChange={(e) => setProfileData({ ...profileData, phone: e.target.value })}
            />

            <select
              className="input"
              onChange={(e) =>
                setProfileData({ ...profileData, educationLevel: e.target.value })
              }
            >
              <option value="">Education Level *</option>
              {[
                "9th pursuing","9th completed","10th pursuing","10th completed",
                "11th pursuing","11th completed","12th pursuing","12th completed",
                "Graduation pursuing","Graduation completed",
              ].map((v) => <option key={v}>{v}</option>)}
            </select>

            {(profileData.educationLevel.includes("11") ||
              profileData.educationLevel.includes("12") ||
              profileData.educationLevel.includes("Graduation")) && (
              <select
                className="input"
                onChange={(e) =>
                  setProfileData({ ...profileData, field: e.target.value })
                }
              >
                <option value="">Field *</option>
                {["Science","Arts","Commerce","BA","BSC","BCOM","BTECH","BCA","LAW","MBBS"]
                  .map((v) => <option key={v}>{v}</option>)}
              </select>
            )}
          </div>

          {/* MARKS */}
          {getMarksFields().length > 0 && (
            <div className="mt-8">
              <h3 className="font-semibold mb-4">üìä Marks (%)</h3>
              <div className="grid md:grid-cols-3 gap-4">
                {getMarksFields().map((cls) => (
                  <input
                    key={cls}
                    placeholder={`${cls} %`}
                    className="input"
                    onChange={(e) =>
                      setProfileData({
                        ...profileData,
                        marks: { ...profileData.marks, [cls]: e.target.value },
                      })
                    }
                  />
                ))}
              </div>
            </div>
          )}

          <button
            onClick={() => setStep(2)}
            className="mt-10 w-full py-4 rounded-xl bg-indigo-600 text-white font-semibold hover:scale-105 transition"
          >
            Continue to Questions ‚Üí
          </button>
        </motion.section>
      )}

      {/* ================= QUESTIONS PAGE (UNCHANGED LOGIC) ================= */}
      {step === 2 && (
        <div className="max-w-4xl mx-auto px-4 pb-24">
          <div className="h-2 bg-white/40 rounded-full mb-8">
            <motion.div
              className="h-full bg-gradient-to-r from-indigo-500 to-purple-500"
              animate={{ width: `${progress}%` }}
            />
          </div>

          <AnimatePresence mode="wait">
            <motion.div
              key={currentQuestion.id}
              {...pageFade}
              className="bg-white/80 dark:bg-slate-900/70 rounded-3xl shadow-xl p-8"
            >
              <p className="text-sm opacity-70 mb-2">
                Question {currentQuestionIdx + 1} of {questions.length}
              </p>

              <h3 className="text-lg font-semibold mb-6">
                {currentQuestion.text}
              </h3>

              <div className="space-y-4">
                {currentQuestion.options.map((opt, idx) => {
                  const active = answers[currentQuestion.id] === idx;
                  return (
                    <motion.label
                      key={idx}
                      className={`block p-4 rounded-xl border cursor-pointer
                      ${active ? "bg-indigo-500 text-white" : "bg-white dark:bg-slate-800"}`}
                    >
                      <input
                        type="radio"
                        className="hidden"
                        checked={active}
                        onChange={() => handleSelect(currentQuestion.id, idx)}
                      />
                      {typeof opt === "string" ? opt : opt.label}
                    </motion.label>
                  );
                })}
              </div>

              <div className="flex justify-between mt-10">
                <button
                  disabled={currentQuestionIdx === 0}
                  onClick={() => setCurrentQuestionIdx((p) => p - 1)}
                  className="px-6 py-3 rounded-xl bg-slate-200 dark:bg-slate-700"
                >
                  Previous
                </button>

                {currentQuestionIdx < questions.length - 1 ? (
                  <button
                    onClick={() => setCurrentQuestionIdx((p) => p + 1)}
                    className="px-6 py-3 rounded-xl bg-indigo-500 text-white"
                  >
                    Continue ‚Üí
                  </button>
                ) : (
                  <button
                    onClick={handleSubmit}
                    className="px-6 py-3 rounded-xl bg-green-500 text-white"
                  >
                    Submit Assessment
                  </button>
                )}
              </div>
            </motion.div>
          </AnimatePresence>
        </div>
      )}

      <footer className="text-center py-6 text-sm opacity-60">
        ¬© 2026 CollegeReviewZ
      </footer>
    </div>
  );
};

export default AssessmentPage;
