import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import questions from "../data/questions";
import { runAssessment } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import { buildPayload } from "../utils/payloadBuilder";

/* ------------------ ANIMATION ------------------ */
const pageFade = {
  initial: { opacity: 0, y: 40 },
  animate: { opacity: 1, y: 0 },
  exit: { opacity: 0, y: -40 },
};

/* ------------------ QUESTION GROUPS ------------------ */
const QUESTION_SECTIONS = [
  { name: "COGNITIVE", start: 0, end: 11 },
  { name: "NUMERACY", start: 12, end: 19 },
  { name: "ACADEMIC", start: 20, end: 29 },
  { name: "VERBAL", start: 30, end: 35 },
  { name: "INTEREST", start: 36, end: 43 },
  { name: "DISCIPLINE", start: 44, end: 51 },
  { name: "RISK", start: 52, end: 59 },
];

const AssessmentPage = () => {
  const navigate = useNavigate();
  const [darkMode, setDarkMode] = useState(false);
  const [step, setStep] = useState("profile");

  /* ------------------ PROFILE STATE ------------------ */
  const [profileData, setProfileData] = useState({
    name: "",
    phone: "",
    educationLevel: "",
    educationStatus: "",
    field: "",
    marks: {},
    stream: "",
    board: "",
    language: "",
    budget: "",
    abroad: "",
  });

  /* ------------------ QUESTION STATE ------------------ */
  const [answers, setAnswers] = useState({});
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  /* ------------------ HELPERS ------------------ */
  const handleProfileChange = (e) => {
    setProfileData((p) => ({ ...p, [e.target.name]: e.target.value }));
  };

  const handleMarksChange = (key, value) => {
    setProfileData((p) => ({
      ...p,
      marks: { ...p.marks, [key]: value },
    }));
  };

  const isProfileComplete =
    profileData.name &&
    profileData.phone &&
    profileData.educationLevel &&
    profileData.educationStatus &&
    profileData.stream &&
    profileData.board &&
    profileData.language &&
    profileData.budget &&
    profileData.abroad &&
    Object.values(profileData.marks).every(Boolean);

  const handleSelect = (qid, optionIndex) => {
    setAnswers((p) => ({ ...p, [qid]: optionIndex }));
  };

  const nextQuestion = () => {
    setCurrentQuestionIdx((p) => p + 1);
  };

  const prevQuestion = () => {
    if (currentQuestionIdx > 0) setCurrentQuestionIdx((p) => p - 1);
  };

  const handleSubmit = async () => {
    try {
      const payload = buildPayload({ profileData, answers });
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch {
      alert("Assessment failed");
    }
  };

  const currentQuestion = questions[currentQuestionIdx];
  const currentSection = QUESTION_SECTIONS.find(
    (s) =>
      currentQuestionIdx >= s.start && currentQuestionIdx <= s.end
  );

  const isSectionEnd =
    currentQuestionIdx === currentSection.end;

  /* ------------------ RENDER ------------------ */
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 to-purple-100 dark:from-slate-900 dark:to-black">

      {/* HEADER */}
      <header className="fixed top-0 w-full z-50 backdrop-blur bg-white/70 dark:bg-black/60">
        <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between">
          <h1 className="font-bold text-xl">CollegeReviewZ</h1>
          <button onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? "‚òÄÔ∏è" : "üåô"}
          </button>
        </div>
      </header>

      <div className="h-28" />

      {/* ================= PROFILE ================= */}
      {step === "profile" && (
        <motion.section {...pageFade} className="max-w-4xl mx-auto bg-white dark:bg-slate-900 p-8 rounded-3xl">
          <h2 className="text-xl font-semibold mb-6">Student Details</h2>

          <div className="grid md:grid-cols-2 gap-5">
            <input name="name" placeholder="Full Name *" onChange={handleProfileChange} className="input" />
            <input name="phone" placeholder="Phone Number *" onChange={handleProfileChange} className="input" />

            <select name="educationLevel" onChange={handleProfileChange} className="input">
              <option value="">Education Level *</option>
              {["9th","10th","11th","12th","Graduation"].map(v => <option key={v}>{v}</option>)}
            </select>

            <select name="educationStatus" onChange={handleProfileChange} className="input">
              <option value="">Status *</option>
              <option>Pursuing</option>
              <option>Completed</option>
            </select>

            {(profileData.educationLevel === "11th" ||
              profileData.educationLevel === "12th" ||
              profileData.educationLevel === "Graduation") && (
              <select name="field" onChange={handleProfileChange} className="input">
                <option value="">Field *</option>
                {profileData.educationLevel === "Graduation"
                  ? ["BA","BSc","BCom","BTech","BCA","LAW","MBBS"]
                  : ["Science","Arts","Commerce"]
                }.map(f => <option key={f}>{f}</option>)
              </select>
            )}

            {Object.keys(profileData.marks).map((k) => (
              <input
                key={k}
                placeholder={`${k} %`}
                onChange={(e) => handleMarksChange(k, e.target.value)}
                className="input"
              />
            ))}

            <input name="stream" placeholder="Stream *" onChange={handleProfileChange} className="input" />
            <input name="board" placeholder="Board *" onChange={handleProfileChange} className="input" />

            <select name="language" onChange={handleProfileChange} className="input">
              <option value="">Language *</option>
              {["Assamese","Bengali","Hindi","Tamil","Telugu","Urdu","Marathi"].map(l => <option key={l}>{l}</option>)}
            </select>

            <select name="budget" onChange={handleProfileChange} className="input">
              <option value="">Budget *</option>
              {["5-6","6-7","7-10","10-15","15-20","20-30"].map(b => <option key={b}>{b} Lakhs</option>)}
            </select>

            <select name="abroad" onChange={handleProfileChange} className="input">
              <option value="">Open To Abroad *</option>
              <option>No</option>
              <option>Yes</option>
            </select>
          </div>

          <div className="flex justify-end mt-8">
            <button
              disabled={!isProfileComplete}
              onClick={() => setStep("questions")}
              className="px-8 py-3 bg-indigo-500 text-white rounded-xl disabled:opacity-40"
            >
              Save & Continue ‚Üí
            </button>
          </div>
        </motion.section>
      )}

      {/* ================= QUESTIONS ================= */}
      {step === "questions" && (
        <div className="max-w-4xl mx-auto p-6">
          <h3 className="text-lg font-semibold mb-4">
            Section: {currentSection.name}
          </h3>

          <motion.div {...pageFade} className="bg-white dark:bg-slate-900 p-8 rounded-3xl">
            <h4 className="mb-6">{currentQuestion.text}</h4>

            <div className="space-y-3">
              {currentQuestion.options.map((opt, idx) => (
                <label key={idx} className={`block p-4 rounded-xl cursor-pointer border ${answers[currentQuestion.id] === idx ? "bg-indigo-500 text-white" : ""}`}>
                  <input
                    type="radio"
                    className="hidden"
                    checked={answers[currentQuestion.id] === idx}
                    onChange={() => handleSelect(currentQuestion.id, idx)}
                  />
                  {typeof opt === "string" ? opt : opt.label}
                </label>
              ))}
            </div>

            <div className="flex justify-between mt-8">
              <button onClick={prevQuestion} disabled={currentQuestionIdx === 0}>
                Previous
              </button>

              {isSectionEnd ? (
                currentQuestionIdx === questions.length - 1 ? (
                  <button onClick={handleSubmit}>Submit Assessment</button>
                ) : (
                  <button onClick={nextQuestion}>Save & Continue ‚Üí</button>
                )
              ) : (
                <button onClick={nextQuestion}>Next ‚Üí</button>
              )}
            </div>
          </motion.div>
        </div>
      )}
    </div>
  );
};

